INSERT INTO BOOKS(book_id,available,description,image,published,title) VALUES(null,true,'William nie jest mężczyzną w typie Olimpii. Wydaje się, że ich znajomość skończy się po jednym spacerze ulicami Torunia, ale William ma wobec dziewczyny inne plany i nie spocznie, dopóki nie osiągnie celu. To opowieść o Kopciuszku, w której nie ma złej macochy, ale jest okrutny ojciec. Jest bajkowa miłość, ale są i całkiem zwyczajne problemy. Jest mnóstwo śmiałych scen, które wywołują wypieki na twarzy, ale i tak najważniejsza jest miłość, która jest w stanie pokonać wszystkie przeciwności losu.','https://cdn-lubimyczytac.pl/upload/books/4955000/4955248/874600-352x500.jpg',2021,'Nieosiągalny');
