INSERT INTO BOOKS(book_id,available,description,image,published,title) VALUES(null,true,'Barack ObamaJeśli chcesz się dowiedzieć, co kierowało byłym prezydentem podczas podejmowania decyzji, które miały zaważyć na losach całego amerykańskiego społeczeństwa, koniecznie sięgnij po "Ziemię obiecaną".Droga Baracka Obamy nigdy nie była idealna. Na każdym kroku, szczególnie po objęciu prezydentury, pojawiały się okoliczności, które przynosiły ze sobą mnóstwo wahań i rozczarowań. Autor najnowszej książki podkreśla, że one również były ważne, by kraj rósł w siłę i stale się rozwijał.','https://cf1-taniaksiazka.statiki.pl/images/large/21A/9788326845833.jpg',2021,'Ziemia obiecana');
